function updateUIForPending(){if(localStorage.getItem("todos")){const t=JSON.parse(localStorage.getItem("todos")).filter(t=>1!=t.completed);if(todoList.innerHTML="",t.length>0)t.forEach(t=>{const e=ce("li");e.classList.add("list-item");const d=ce("div");d.addEventListener("click",handleComplete),d.id=t.id,d.classList.add("circle");const o=ce("div");o.classList.add("text"),o.textContent=t.todo,appendNode(e,d),appendNode(e,o),appendNode(todoList,e)});else{const t=ce("li");t.classList.add("list-item");const e=ce("div");e.classList.add("text"),e.textContent="No Pending todos",appendNode(t,e),appendNode(todoList,t)}}}function updateUIForCompleted(){if(localStorage.getItem("todos")){const t=JSON.parse(localStorage.getItem("todos")).filter(t=>0!=t.completed);if(todoList.innerHTML="",t.length>0)t.forEach(t=>{const e=ce("li");e.classList.add("list-item");const d=ce("div");d.addEventListener("click",handleComplete),d.id=t.id,d.classList.add("complete");const o=ce("i");o.className="fas fa-check",appendNode(d,o),d.classList.add("circle");const s=ce("div");s.classList.add("text"),s.textContent=t.todo,appendNode(e,d),appendNode(e,s),appendNode(todoList,e)});else{const t=ce("li");t.classList.add("list-item");const e=ce("div");e.classList.add("text"),e.textContent="None completed",appendNode(t,e),appendNode(todoList,t)}}}function handleListChange(t){"All "==t?updateUI():"Pending "==t?updateUIForPending():updateUIForCompleted()}function handleTabChange(t){tabs.forEach(t=>{t.classList.remove("active")}),t.target.classList.add("active"),handleListChange(t.target.textContent)}const tabs=document.querySelectorAll(".tab");tabs.forEach(t=>{t.addEventListener("click",handleTabChange)});